private void drawEyes (GOval head) {
        //right eye
        double x = head.getX() + Math.cos(Math.toRadians(-45)) * RADIUS + RADIUS - RADIUS_EYE / 2;
        double y = head.getY() + Math.sin(Math.toRadians(-45)) * RADIUS + RADIUS;
        draw1Eye(x, y);
        //left eye
        x = head.getX() + Math.cos(Math.toRadians(-90-45)) * RADIUS + RADIUS - RADIUS_EYE / 2;
        y = head.getY() + Math.sin(Math.toRadians(-90-45)) * RADIUS + RADIUS;
        draw1Eye(x, y);
    }

