private string isBounceWall(){

					if(newX <= 0)  return "left";	//шар ударится в левую стенку

					if(newX  >= WIDTH - 2 * BALL_RADIUS) return "right";

					if(newY  <= 0) return "up";

					if (newY >= HEIGHT - 2 * BALL_RADIUS) return "down";

					return "";
				}

