public void goToNewspaper(){
  goToBlock();
  turnToRight();
  while( leftIsBlocked()){//двигаться пока слева стена
    move();
  }
  turnLeft();
  goToBeeper();
 }

private void turnToBack(){
  turnToLeft();
  turnToLeft();
}

private void goToHome(){
  turnToBack();
  goToBlock();
  turnToRight();
  goToBlock();
}

public void run() {
  goToNewspaper ();
  pickBeeper();
  goToHome();
}

