public void goToBeeper(){
  goToBlock();
  turnToRight();
  while( leftIsBlocked()){//двигаться пока слева стена
    move();
  }
  turnLeft();
  goToBeeper();
 }

/* Предусловия: Карел куда-то смотрит.  
     * Результат: Карел смотрит в ту же сторону, 
     * но при этом сделал шаг назад.  
     */
    private void moveBackward() {
        turnAround();
        move();
        turnAround();
    }

/* Предусловия: Карел смотрит на клетку, которую надо почистить
     * Результат: Карел там же, но клетка, на которую 
     * он смотрел - очищена.  
     */
    private void cleanChad() {
        move();
        while (beepersPresent()) {
            pickBeeper();
        }
        moveBackward();
    }

/* Предусловия: Карел в центре элемента реактора, смотрит на восток
     * Результат: Карел в центре либо чистого элемента, либо 
     * функционирующего, смотрит на восток и улыбается
     */
    private void processRectangle() {
        /* Если в центре элемента пусто, то время чистить 
         * остатки ядерных реакций по краям
         */
        if (noBeepersPresent()) {
            removeAllChad();
        }
    }

/* Предусловия: Карел в центре элемента, который надо чистить
     * Результат: Карел в центре чистого элемента
     */
    private void removeAllChad() {
        /* Вычистить верхний угол */
        turnLeft();
        cleanChad();
        /* Вычистить нижний угол */
        turnAround();
        cleanChad();
        /* Развернуть Карела на восток */
        turnLeft();
    }
